import React, { Component } from "react";
// import axios from "axios";

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      formValues: {
        username: "",
        password: "",
      },
      formError: {},
    };
    this.onInputChange = this.onInputChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  onInputChange(e) {
    this.setState({
      formValues: { ...this.state.formValues, [e.target.name]: e.target.value },
    });
    console.log(this.state.formValues);
  }

  onSubmit(e) {
    e.preventDefault();
    this.setState({ formError: this.validate(this.state.formValues) });
    console.log(this.state.formError);
    //   axios
    //     .post("http://localhost:3001/signUp", this.state.formValues)
    //     .then((res) => console.log(res.status))
    //     .catch((err) => {
    //       console.error(err);
    //     });
  }

  validate(value) {
    const error = {};
    if (!value.username) {
      error.username = "username is required!";
    }
    if (!value.password) {
      error.password = "password is required!";
    }
    return error;
  }

  render() {
    return (
      <div>
        <pre>{JSON.stringify(this.state.formValues)}</pre>
        <form onSubmit={this.onSubmit} className="container">
          <h3>Login</h3>
          <div className="form-group">
            <label>username</label>
            <input
              onChange={this.onInputChange}
              type="text"
              className="form-control"
              placeholder="username"
              name="username"
            />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input
              onChange={this.onInputChange}
              type="text"
              className="form-control"
              placeholder="password"
              name="password"
            />
          </div>
          <button type="submit" className="btn btn-primary btn-block">
            Login
          </button>
          <p className="forgot-password text-right">
            Don't have account? <a href="#">sign up?</a>
          </p>
        </form>
      </div>
    );
  }
}

export default Login;
